    private void btnSortActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSortActionPerformed
        String arrayText = tfArray.getText();
        String[] subArrayText = arrayText.split(",");
        int[] data = new int[subArrayText.length];
        int[] unsorted = data;
        for (int i = 0; i < subArrayText.length; i++)
        {
            data[i] = Integer.parseInt(subArrayText[i]);
        }
        SortingContext sortingContext = new SortingContext();
        int sortedArray [] = sortingContext.sort(data);
        lblArraySize.setText(String.valueOf(data.length));
        lblSortedArray.setText(String.valueOf(sortedArray));
        String sortName = sortingContext.getSortingStrategyName(data.length);
        lblSortType.setText(sortName);
        SorterController.addToStrategyCollection(unsorted, data.length, sortName, sortedArray);
    }//GEN-LAST:event_btnSortActionPerformed

