    private void btnLogInActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLogInActionPerformed
        Usser usser;
        Usser confirmedUsser;
        LogInController logInController;
        
        usser = new Usser(txtUsserName.getText(),txtPassword.getText(),null);
        logInController = new LogInController();
        
        confirmedUsser = logInController.confirmUsser(usser);
      
        if(confirmedUsser == null){
            JOptionPane.showMessageDialog(this, "Please, try again.");
            
        }else if("administrator".equals(confirmedUsser.getStatus())){
            FrmChangeTax frmChangeTax = new FrmChangeTax();
            frmChangeTax.setVisible(true);
            this.setVisible(false);
            
        }else if("commonUsser".equals(confirmedUsser.getStatus())){
            FrmTaxCalculator frmTaxCalculator = new FrmTaxCalculator();
            frmTaxCalculator.setVisible(true);
            this.setVisible(false);
        }
            
        
    }//GEN-LAST:event_btnLogInActionPerformed

