    private void txtValueObjectKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtValueObjectKeyTyped
        if(!txtValueObject.getText().isEmpty()){
            btnCalculate.setEnabled(true);
        }
        if(txtValueObject.getText().isEmpty()){
            btnCalculate.setEnabled(false);
        }
    }//GEN-LAST:event_txtValueObjectKeyTyped

    private void btnCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateActionPerformed
        USTax utahTax = USTax.getInstance(0.0485F);
        utahTax.setTaxValue(0.0485F);
        USTaxController utahTaxTotal = new USTaxController();
        txtTotalUtah.setText(Float.toString(utahTaxTotal.salesTotal(Float.parseFloat(txtValueObject.getText()), utahTax.getTaxValue())));
    }//GEN-LAST:event_btnCalculateActionPerformed

