    private void btnCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateActionPerformed
        float productPrice;
        float taxPercentage;
        float totalProductPrice;
        float totalProductPrice2Decimal;
        
        USTax tax = USTax.getInstance();
        
        productPrice = Float.parseFloat(txtPriceProduct.getText());
        taxPercentage = Float.parseFloat(txtTaxPercentage.getText());
        
        tax.modifyTax(taxPercentage);
        totalProductPrice = tax.salesTotal(productPrice);
        totalProductPrice2Decimal = Math.round(totalProductPrice * 100.0F)/100.0F;
        
        lblProductPrice.setText(String.valueOf(productPrice) + "$");
        lblTaxPercentage.setText(String.valueOf(taxPercentage) + "%");
        lblTotalProductPrice.setText(String.valueOf(totalProductPrice2Decimal) + "$");
    }//GEN-LAST:event_btnCalculateActionPerformed

