    private void btnSaveIvaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSaveIvaActionPerformed
        UtahTax utahTax = UtahTax.getInstance();
        float newIva;
        try {
            newIva = Float.parseFloat(txtIva.getText());
            utahTax.modifyIva(newIva);
        } catch (Exception e) {
        }
        
    }//GEN-LAST:event_btnSaveIvaActionPerformed

