    private void btnEnterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEnterActionPerformed
        // TODO add your handling code here:
        String user = txtUser.getText();
        String password = txtPassword.getText();

        if (user.equals("user") && password.equals("1234")) {
            FrmChangeTax btnEvent = new FrmChangeTax();
            btnEvent.setVisible(true);
            this.dispose();
        }else{
            JOptionPane.showMessageDialog(this, "The credencials inserted doesn't exist", "ERROR", JOptionPane.WARNING_MESSAGE);
            txtUser.setText("");
            txtPassword.setText("");
        }
    }//GEN-LAST:event_btnEnterActionPerformed

