    private void txtFloridaValueKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtFloridaValueKeyTyped
        // TODO add your handling code here:
        if(!txtFloridaValue.getText().isEmpty()){
            btmCalculate.setEnabled(true);
        }
        if(txtFloridaValue.getText().isEmpty()){
            btmCalculate.setEnabled(false);
        }
    }//GEN-LAST:event_txtFloridaValueKeyTyped

    private void btmCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btmCalculateActionPerformed
        // TODO add your handling code here:
        USTax utahTax = USTax.getInstance(0.0F);
        utahTax.setTaxValue(0.06F);
        USTaxController utahTaxTotal = new USTaxController();
        lbTotal.setText(Float.toString(utahTaxTotal.salesTotal(Float.parseFloat(txtFloridaValue.getText()), utahTax.getTaxValue())));
        
    }//GEN-LAST:event_btmCalculateActionPerformed

