    private void btnCalculateTotalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateTotalActionPerformed
        // TODO add your handling code here:
        float price;
        float taxPercentage;
        price = Float.parseFloat(txtPrice.getText());
        taxPercentage = Float.parseFloat(txtTax.getText());
        
        USTax tax = USTax.getInstance();
        
        tax.modifyTax(taxPercentage);
        
        lblPrice.setText(String.valueOf(price));
        lblTax.setText(String.valueOf(taxPercentage));
        lblTotal.setText(String.valueOf(tax.salesTotal(price)));
        
    }//GEN-LAST:event_btnCalculateTotalActionPerformed

