    private void btnCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateActionPerformed
        // TODO add your handling code here:
        float price, total;

        USTax tax = USTax.getInstance();

        tax.modifyTax(taxUtah);

        price = Float.parseFloat(String.valueOf(textPrice.getText()));
        System.out.println("" + price);

        btnCalculate.setEnabled(true);
        total = tax.salesTotal(price);

        jlbPriceprint.setText("" + price);
        jlbPriceTotal.setText("" + total);

        emptyFields();

        //tax.modifyTax(Float.parseFloat(String.copyValueOf(jlbTaxPercentage.getValue())));
    }//GEN-LAST:event_btnCalculateActionPerformed

