    private void txtAUnorderedNumbersKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtAUnorderedNumbersKeyTyped
        char character = evt.getKeyChar();

        if (!Character.isDigit(character) && character!=44 && (character != KeyEvent.VK_BACK_SPACE) && (character != KeyEvent.VK_DELETE) && (character != KeyEvent.VK_SPACE)  && (character != KeyEvent.VK_ENTER)) {
            JOptionPane.showMessageDialog(this, character + " No es aceptado", "Advertencia en el ingreso de datos", JOptionPane.WARNING_MESSAGE);
            evt.consume();
        }
    }//GEN-LAST:event_txtAUnorderedNumbersKeyTyped

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        txtAOrderedNumbers.setText("");
        txtAUnorderedNumbers.setText("");
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        String[] numbers = txtAUnorderedNumbers.getText().split(",");
        int data[] = new int[numbers.length];
        for (int i = 0; i < numbers.length; i++) {
            data[i] = Integer.parseInt(numbers[i].trim());
        }
        
        SortingContext sc = new SortingContext();
        int sortedList[] = sc.sort(data);  
        
        txtAOrderedNumbers.setText(Arrays.toString(sortedList));
    }//GEN-LAST:event_jButton2ActionPerformed

