    private void btnAcceptActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAcceptActionPerformed
        double rentTaxValue = TaxOperation.computeRentTax(Double.parseDouble(txtfSalary.getText()), Double.parseDouble(txtfCosts.getText()));
        txtfRentTax.setText(String.valueOf(rentTaxValue));
    }//GEN-LAST:event_btnAcceptActionPerformed

