    private void btnBackActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBackActionPerformed
        FrmCalculatorMenu frmCalculator = new FrmCalculatorMenu();
        frmCalculator.setVisible(true);
        this.setVisible(false);
    }//GEN-LAST:event_btnBackActionPerformed

    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
        float addend1 = Float.parseFloat(txtAddend1.getText());
        float addend2 = Float.parseFloat(txtAddend2.getText());
        
        BasicOperation operation = new BasicOperation();
        
        float sum = operation.add(addend1, addend2);
        
        lblSum.setText(String.valueOf(sum));
    }//GEN-LAST:event_btnAddActionPerformed

