    private void btmFindActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btmFindActionPerformed
        StudentController studentController = new StudentController();
        Student student = null;
        
        int id = Integer.valueOf(txtId.getText());
        student = studentController.find(id);
        
        if (student.getId() != 0) {
            btmSave.setEnabled(true);
            txtUnit1.setText(String.valueOf(student.getGrades()[0]));
            txtUnit2.setText(String.valueOf(student.getGrades()[1]));
            txtUnit3.setText(String.valueOf(student.getGrades()[2]));
            
        } else {
            btmSave.setEnabled(false);
            emptyFields();
        }
    }//GEN-LAST:event_btmFindActionPerformed
    
    private void emptyFields() {
        txtUnit1.setText("");
        txtUnit2.setText("");
        txtUnit3.setText("");
    }
    

    private void btmSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btmSaveActionPerformed
        StudentController studentController = new StudentController();
        float grades[] = {Float.parseFloat(txtUnit1.getText()), Float.parseFloat(txtUnit2.getText()), Float.parseFloat(txtUnit1.getText())};
        float GPA = studentController.computeAverage(grades);
        txtGPA.setText(String.valueOf(GPA));
        int desicion = JOptionPane.showConfirmDialog(this, "Are you want to save grades?");
        
        if(desicion == 0){
            emptyFields();
            txtGPA.setText("");
            btmSave.setEnabled(false);
        }
        if(desicion == 1){
            emptyFields();
            txtGPA.setText("");
            btmSave.setEnabled(false );
        }
        if(desicion == 2){
            
        }
    }//GEN-LAST:event_btmSaveActionPerformed

