    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
        BasicOperation basicOperation = new BasicOperation();
        float sum, addend1, addend2;
        addend1 = Float.parseFloat(txtDigit1.getText());
        addend2 = Float.parseFloat(txtDigit2.getText());
        sum = basicOperation.add(addend1, addend2);
        txtResult.setText(String.valueOf(sum));
    }//GEN-LAST:event_btnAddActionPerformed

    private void btnSubtractActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSubtractActionPerformed
        BasicOperation basicOperation = new BasicOperation();
        float difference, minued, substraend;
        minued = Float.parseFloat(txtDigit1.getText());
        substraend = Float.parseFloat(txtDigit2.getText());
        difference = basicOperation.substract(minued, substraend);
        txtResult.setText(String.valueOf(difference));
    }//GEN-LAST:event_btnSubtractActionPerformed

    private void btnMultiplyActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMultiplyActionPerformed
        BasicOperation basicOperation = new BasicOperation();
        float product, multiplicand, multiplier;
        multiplicand = Float.parseFloat(txtDigit1.getText());
        multiplier = Float.parseFloat(txtDigit2.getText());
        product = basicOperation.multiply(multiplicand, multiplier);
        txtResult.setText(String.valueOf(product));
    }//GEN-LAST:event_btnMultiplyActionPerformed

    private void btnDivideActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDivideActionPerformed
        BasicOperation basicOperation = new BasicOperation();
        float quotient, dividend, divisor;
        dividend = Float.parseFloat(txtDigit1.getText());
        divisor = Float.parseFloat(txtDigit2.getText());
        quotient = basicOperation.divide(dividend, divisor);
        txtResult.setText(String.valueOf(quotient));
    }//GEN-LAST:event_btnDivideActionPerformed

    private void btnExitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExitActionPerformed
        this.dispose();
    }//GEN-LAST:event_btnExitActionPerformed

